import * as mongoose from 'mongoose';

const schema = new mongoose.Schema({
    name: String,
    description: String,
    name_slug: {type: String, index: true},
    is_active: {type: Boolean, default: true}
}, {
    timestamps: {
        createdAt: 'created_at',
        updatedAt: 'updated_at'
    }
});

schema.virtual('id').get(function () {
    return this._id.toHexString();
});

export interface AuthorModelInterface extends mongoose.Document {
    name: string
    description: string
    name_slug: string
    is_active: boolean
    created_at: Date
    updated_at: Date
}

interface AuthorStaticInterface extends mongoose.Model<AuthorModelInterface> {
    AuthorModelInterface: AuthorModelInterface
}


export default mongoose.model<AuthorModelInterface, AuthorStaticInterface>('author', schema, 'author');