import * as mongoose from 'mongoose';

const schema = new mongoose.Schema({
    name: { type: String, index: true },
    icon_url: String,
    api_key: String,
    api_secret: String,
    api_url: { type: String, unique: true },
    web_url: { type: String, index: true },
    description: String,
    is_active: { type: Boolean, default: true }

});


schema.virtual('id').get(function () {
    return this._id.toHexString();
});


export interface BankPartnerModelInterface extends mongoose.Document {
    name: string
    icon_url: string
    is_active: boolean
    api_key: string
    api_secret: string
    api_url: string
    web_url: string
    description: string
}


export default mongoose.model<BankPartnerModelInterface>('mission_partner', schema, 'mission_partner');