import * as mongoose from 'mongoose';

const schema = new mongoose.Schema({
    title: String,
    description: String,
    thumbnail: String,
    content: String,
    begin_time: {type: Number, default: 0},
    end_time: {type: Number, default:0},
},{
    timestamps: {
        createdAt:'created_at',
        updatedAt: 'updated_at'
    }
});

schema.virtual('id').get(function () {
    return this._id.toHexString();
});

export interface EventModelInterface extends mongoose.Document{
    title: string
    description: string
    thumbnail: string
    content: string
    begin_time: number
    end_time: number
    created_at: Date
    updated_at: Date
}

export default  mongoose.model<EventModelInterface>('event', schema, 'event');