import * as mongoose from 'mongoose';

const schema = new mongoose.Schema({
    name: {type: String, unique: true},
    phone_number: String,
    name_slug: {type: String, index: true},
    address: String,
    is_active: {type: Boolean, default: true}
},{
    timestamps: {
        createdAt: 'created_at',
        updatedAt: 'updated_at'
    }
});

schema.virtual('id').get(function () {
    return this._id.toHexString();
});

export interface IssuingCompanyModelInterface extends mongoose.Document {
    name: string
    phone_number: string
    is_active: boolean
    name_slug: string
    address: string,
    created_at: Date
    updated_at: Date
}

export default mongoose.model<IssuingCompanyModelInterface>('issuing_company', schema, 'issuing_company');