import * as mongoose from 'mongoose';

const schema = new mongoose.Schema({
    name: {type: String, unique: true},
    phone_number: String,
    name_slug: {type: String, index: true},
    is_active: {type: Boolean, default: true},
    address: String,
}, {
    timestamps: {
        createdAt: 'created_at',
        updatedAt: 'updated_at'
    }
});

schema.virtual('id').get(function () {
    return this._id.toHexString();
});

export interface PublisherModelInterface extends mongoose.Document {
    name: string
    phone_number: string,
    name_slug: string
    is_active: boolean
    address: string,
    created_at: Date,
    updated_at: Date
}

export default mongoose.model<PublisherModelInterface>('publisher', schema, 'publisher');