import * as mongoose from 'mongoose';


const schema = new mongoose.Schema({
    fromKM: { type: Number, default: 0 },
    toKM: { type: Number, default: 0 },
    is_active: { type: Boolean, default: true },
    cost: { type: Number, default: 0 }
}, {
    timestamps: {
        createdAt: 'created_at',
        updatedAt: 'updated_at'
    }
});

schema.virtual('id').get(function () {
    return this._id.toHexString();
});

export interface ShippingCostModelInterface extends mongoose.Document {
    fromKM: number,
    toKM: number,
    cost: number,
    is_active: boolean
}

export default mongoose.model<ShippingCostModelInterface>('shipping_cost', schema, 'shipping_cost');