import * as mongoose from 'mongoose';
import { UserModelInterface } from '@private/models/User.model';
import { CmsGroupModelInterface } from '@private/models/CmsGroup.model';

const schema = new mongoose.Schema({
    staff: {
        type: mongoose.Schema.Types.ObjectId,
        ref: 'user',
        index: true
    },
    cms_group: {
        type: mongoose.Schema.Types.ObjectId,
        ref: 'cms_group',
        index: true
    }
}, {
    timestamps: {
        createdAt: 'created_at',
        updatedAt: 'updated_at'
    }
});

schema.virtual('id').get(function () {
    return this._id.toHexString();
});

export interface StaffCmsGroupModelInterface extends mongoose.Document {
    staff: mongoose.Types.ObjectId | UserModelInterface,
    cms_group: mongoose.Types.ObjectId | CmsGroupModelInterface
}

export default mongoose.model<StaffCmsGroupModelInterface>('staff_cms_group', schema, 'staff_cms_group');