import axios from 'axios';
import { API_KEY, API_KEY_CHANH_SA } from '@private/configs/index';

const getGeocodingData = function (address) {
    address = encodeURIComponent(address);
    console.log('url', `https://maps.googleapis.com/maps/api/geocode/json?address=${address}&key=${API_KEY_CHANH_SA}`);
    return axios.get(`https://maps.googleapis.com/maps/api/geocode/json?address=${address}&key=${API_KEY_CHANH_SA}`)
        .then(function (data) {
            return data.data;
        });
};

export default {
    getGeocodingData
};